import {Source, Img} from '../components/index';

const Picture = (props) => {
    return (
        <picture className={props.className}>
            <Source media={props.media[0]} media={props.media[1]} mediaSrc={props.mediaSrc} />
            <Img src={props.src}/>
        </picture>
    );
};

export default Picture;
