import {Source, Img} from '../components/index';

const Picture = (props) => {
    return (
        <picture className={props.className}>
            <Source media={'(min-width: 600px)'} mediaSrc={'./mainvisual-sp.jpg'} />
            <Img src={'mainvisual-pc.jpg'}/>
        </picture>
    );
};

export default Picture;
